package sec03.exam01;

public class LogicalOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 20;
		if((x == 10) && (y == 20)) {
			System.out.println("(x == 10) && (y == 20) 구문은 맞습니다.");
		}
		
		if((x == 10) || (y == 10)) {
			System.out.println("(x == 10) || (y == 10) 구문은 맞습니다.");
		}
	}

}
